package gfgselfplaced.strings;

public class ConcatinateStrings {
    public static void main(String[] args) {
        System.out.println(concatenatedString("","enrarreilbkffrvuywmmoxwejp"));
    }
    public static String concatenatedString(String s1,String s2)
    {
        int n = s1.length();
        int  m= s2.length();
        int[] freq = new int[256];
        for(int i=0; i<n; i++) {
            if(freq[s1.charAt(i)] == 0) {
                freq[s1.charAt(i)]++;
            }
        }
        for(int i=0; i<m; i++) {
                freq[s2.charAt(i)]--;
        }

        StringBuilder result = new StringBuilder();
        for(char c : s1.toCharArray()) {
            if (freq[c] == 1)
                result.append(c);
        }
        for(char c : s2.toCharArray()) {
            if (freq[c] == 1)
                result.append(c);
        }
        return (result.length() > 0) ? result.toString() : "-1";
    }
}
